startup:
if [[ ! -z ${VERSION} ]]; then
    echo -e \"Using version ${VERSION}\";
else
    echo -e \"Please set the VERSION variable \(e.g. v3.0.0\)\";
    exit 0;
fi;

if [[ -d .git ]]; then
    git fetch --all --tags;
    git checkout tags/${VERSION};
fi;

if [[ ! -z ${PLUGINS} ]]; then
    /usr/local/bin/npm install ${PLUGINS};
fi;

if [ -f /home/container/package.json ]; then
 /usr/local/bin/npm install --production;
fi;

/usr/local/bin/npm start

config.startup:
{
    "done": "Connected to Discord as ",
    "userInteraction": [
        "Please set your bot's \"DISCORD_TOKEN\" in \"./.env\"."
    ]
}
